import "./RegisterForm.scss";
import React, { useState } from "react";
import { LayoutDesign } from "../LayoutDesign/LayoutDesign";
const RegisterForm = () => {
  const [formSubmit, setFormSubmit] = useState(false);

  const [row, setRow] = useState(10);
  const [column, setColumn] = useState(10);


  return (
    <>
      {!formSubmit ? (
        <div className="register-form-wrapper">
          <div className="form-container">
            <div className="form">
              <h2 className="form__heading">Registration</h2>
              <div className="form__input">
                <p className="label">Number of Rows</p>
                <div className={`input-container ${(row !== "" || row>0) && "activeInput"}`}>
                <input
                  type="number"
                  value={row}
                  onChange={(e) => {
                    setRow(parseInt(e.target.value));
                  }}
                />

                </div>
              </div>
              <div className="form__input">
                <p className="label">Number of Columns</p>
                <div className={`input-container ${(column !== "") && "activeInput"}`}>
                <input
                  type="number"
                  value={column}
                  onChange={(e) => {
                    setColumn(parseInt(e.target.value));
                  }}
                /></div>
              </div>

              <div className="form-Buttons">
                <button
                  onClick={() => {
                    setFormSubmit(true);
                  }}
                >
                  Submit
                </button>
              </div>
            </div>

          </div>
        </div>
      ) : (
        <LayoutDesign row={row} column={column} />
      )}
    </>
  );
};
export default RegisterForm;
