import React, { useEffect, useState } from 'react'
import "./updatePopup.scss"
import { Cell } from '../Cell/Cell'

export const UpdatePopup = ({ layoutSeleted }) => {
    console.log(layoutSeleted)

    const [layoutData, setLayoutData] = useState(layoutSeleted.companyLayout);

    const updateCellValue = (rowIndex, cellIndex, newValue) => {
        const updatedLayout = [...layoutData];
        updatedLayout[rowIndex][cellIndex] = newValue;
        setLayoutData(updatedLayout);
    };

    return (
        <div>
            <div>
                {layoutData?.map((row, rowIndex) => (
                    <div key={rowIndex}>
                        {row?.map((cell, cellIndex) => (
                            <span
                                key={cellIndex}
                                onClick={() => {
                                    updateCellValue(rowIndex, cellIndex, cell === 1 ? 0 : 1);
                                }}
                                style={{ cursor: 'pointer', marginRight: '5px' }}
                            >
                                {cell}
                            </span>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    )
}
