import React, { useEffect, useState } from 'react'
import "./updatePopup.scss"
import { Cell } from '../Cell/Cell'

export const UpdatePopup = ({ layoutSeleted }) => {
    console.log(layoutSeleted)
    const initialData = {
        layoutId: "d0cc32fe-6e47-4233-9583-170e98e6df77",
        companyName: "demo",
        defaultLayout: [
          [1, 0, 1],
          [1, 1, 1]
        ]
      };

      // Initialize state with the default layout
      const [layoutData, setLayoutData] = useState(layoutSeleted);

      // Function to update a single cell value
      const updateCellValue = (rowIndex, cellIndex, newValue) => {
        const updatedLayout = [...layoutData.defaultLayout];
        updatedLayout[rowIndex][cellIndex] = newValue;
        setLayoutData({
          ...layoutData,
          defaultLayout: updatedLayout
        });
      };

    return (
        <div>
        <h2>{layoutData.companyName} Layout</h2>
        <div>
          {layoutData.defaultLayout.map((row, rowIndex) => (
            <div key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <span 
                  key={cellIndex} 
                  onClick={() => {
                    updateCellValue(rowIndex, cellIndex, cell === 1 ? 0 : 1);
                  }}
                  style={{ cursor: 'pointer', marginRight: '5px' }}
                >
                  {cell}
                </span>
              ))}
            </div>
          ))}
        </div>
      </div>
    )
}
