import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons'

export const Register = () => {
    const [registerValue, setregisterValue] = useState({
        mobileEmail: "",
        fullName: "",
        userName: "",
        password: ""
    })
    const [registerError, setregisterError] = useState({
        mobileEmailError: "",
        fullNameError: "",
        userNameError: "",
        passwordError: "",
        validateError: ""
    })
    const [validregister, setValidregister] = useState({
        mobileEmailValid: false,
        fullName: false,
        userNameValid: false,
        passwordValid: false
    });
    const [registerLoading, setregisterLoading] = useState(false);
    const [registerEyeVisible, setregisterEyeVisible] = useState(false)


    //validating sign in value
    const mobileEmailChange = (e) => {
        const val = e.target.value;
        if (val === "") {
            setregisterError({ ...registerError, mobileEmailError: "Please enter a value" })
            setValidregister({ ...validregister, mobileEmailValid: false })
        }
        //email valid
        else if ((/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(val))) {
            setregisterValue({ ...registerValue, mobileEmail: e.target.value })
            setregisterError({ ...registerError, mobileEmailError: "" })
            setValidregister({ ...validregister, mobileEmailValid: true })
        }
        //phone number valid
        else if (val === "" || /^[0-9]+$/.test(val)) {
            if (val.length === 10) {
                setregisterValue({ ...registerValue, mobileEmail: e.target.value })
                setregisterError({ ...registerError, mobileEmailError: "" })
                setValidregister({ ...validregister, mobileEmailValid: true })
            } else {
                setregisterError({ ...registerError, mobileEmailError: "Please enter a valid phone number" })
                setValidregister({ ...validregister, mobileEmailValid: false })
            }
        }
        else {
            setregisterError({ ...registerError, mobileEmailError: "Please enter a valid email" })
            setValidregister({ ...validregister, mobileEmailValid: false })
        }
    }


    //validating full name value
    const fullNameChange = (e) => {
        const fullname = e.target.value;
        if (fullname === "") {
            setregisterError({ ...registerError, fullNameError: "Please enter a value" })
            setValidregister({ ...validregister, fullNameValid: false })
        }
        //username valid
        else if (!(/^[A-Z][a-z]*( [A-Z][a-z]*)*$/.test(fullname))) {
            if (!/^[A-Z]/.test(fullname)) {
                setregisterError({ ...registerError, fullNameError: "Full name must start with a capital letter" })
                setValidregister({ ...validregister, fullNameValid: false })
            } else if (/\d/.test(fullname)) {
                setregisterError({ ...registerError, fullNameError: "Full name cannot contain numbers" })
                setValidregister({ ...validregister, fullNameValid: false })
            } else if (/[^a-zA-Z\s]/.test(fullname)) {
                setregisterError({ ...registerError, fullNameError: "Full name cannot contain special characters" })
                setValidregister({ ...validregister, fullNameValid: false })
            }
            else {
                setregisterValue({ ...registerValue, fullName: e.target.value })
                setregisterError({ ...registerError, fullNameError: "" })
                setValidregister({ ...validregister, fullNameValid: true })
            }
        }
        else {
            setregisterValue({ ...registerValue, fullName: e.target.value })
            setregisterError({ ...registerError, fullNameError: "" })
            setValidregister({ ...validregister, fullNameValid: true })
        }
    }

    //validating sign in value
    const userNameChange = (e) => {
        const username = e.target.value;
        if (username === "") {
            setregisterError({ ...registerError, userNameError: "Please enter a value" })
            setValidregister({ ...validregister, userNameValid: false })
        }
        //username valid
        else if (/^(?=.*[a-zA-Z])(?=.*[0-9])[a-zA-Z0-9]+$/.test(username)) {
            setregisterValue({ ...registerValue, userName: e.target.value })
            setregisterError({ ...registerError, userNameError: "" })
            setValidregister({ ...validregister, userNameValid: true })
        }
        else if (/[^a-zA-Z\s]/.test(username)) {
            setregisterError({ ...registerError, userNameError: "User name cannot contain special characters" })
            setValidregister({ ...validregister, userNameValid: false })
        }
        else {
            setregisterError({ ...registerError, userNameError: "Username contains alphabets and numbers" })
            setValidregister({ ...validregister, userNameValid: false })
        }
    }


    //validating password 
    const registerPasswordChange = (e) => {

        const pwd = e.target.value;
        if (pwd === "") {
            setregisterError({ ...registerError, passwordError: "Please enter a password" })
            setValidregister({ ...validregister, passwordValid: false })
        } else if (/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{6,}$/.test(pwd) && pwd.length >= 6) {
            setregisterValue({ ...registerValue, password: e.target.value })
            setregisterError({ ...registerError, passwordError: "" })
            setValidregister({ ...validregister, passwordValid: true })
        } else {
            setregisterError({ ...registerError, passwordError: "Password must contain at least one uppercase letter, one lowercase letter and one digit" })
            setValidregister({ ...validregister, passwordValid: false })
        }
    }

    //sign up validate
    const validateregister = (e) => {
        e.preventDefault();
        if (validregister.mobileEmailValid === true && validregister.fullNameValid === true
            && validregister.userNameValid === true && validregister.passwordValid === true) {
            setregisterError({ ...registerError, validateError: "" })
            console.log(registerValue)
            setregisterLoading(true)
            setTimeout(() => {
                setregisterLoading(false);
            }, 2000);
        } else {
            setregisterError({ ...registerError, validateError: "Please enter correct data" })
            setregisterLoading(false)
        }
    }

    return (

        <>
        <div className='heading'>
            Register
        </div>
            {/* Input field of register value */}
            <input className='login-text'
                defaultValue={setregisterValue.mobileEmail === "" ? "" : setregisterValue.mobileEmail}
                onChange={mobileEmailChange}
                type='text'
                placeholder='Mobile number or email address'
                autocomplete="off" />
            <p className='login-error'>{registerError.mobileEmailError}</p>

            {/* Input field of Full Name value */}
            <input className='login-text'
                defaultValue={setregisterValue.fullName === "" ? "" : setregisterValue.fullName}
                onChange={fullNameChange}
                type='text'
                placeholder='Full Name'
                autocomplete="off" />
            <p className='login-error'>{registerError.fullNameError}</p>

            {/* Input field of Username value */}
            <input className='login-text'
                defaultValue={setregisterValue.userName === "" ? "" : setregisterValue.userName}
                onChange={userNameChange}
                type='text'
                placeholder='Username'
                autocomplete="off" />
            <p className='login-error'>{registerError.userNameError}</p>

            {/* Input field of Password value */}
            <div className='sigin-password'>
                <input className='login-text'
                    defaultValue={setregisterValue.password === "" ? "" : setregisterValue.password}
                    onChange={registerPasswordChange}
                    type={registerEyeVisible ? "text" : "password"}
                    placeholder='Password'
                    autocomplete="off" />
                <div className='eye-icon' onClick={() => setregisterEyeVisible(!registerEyeVisible)}>
                    {
                        registerEyeVisible ?
                            <FontAwesomeIcon icon={faEye} /> :
                            <FontAwesomeIcon icon={faEyeSlash} />
                    }
                </div>
            </div>
            <p className='login-error'>{registerError.passwordError}</p>

            {/* Sign Up Button */}
            <button className='login-button' onClick={validateregister}>
                <div>
                    Sign Up
                </div>
            </button>
            {
                registerLoading ?
                    <div className='login-loading'>
                        {/* <img src={loginpage.loading} width="35px" /> */}
                    </div> :
                    <></>
            }
            <p className='login-error'>{registerError.validateError}</p>
        </>
    )
}
