import "./RegisterForm.scss";
import React, { useState } from "react";
import { LayoutDesign } from "../LayoutDesign/LayoutDesign";
const RegisterForm = () => {
  const [formSubmit, setFormSubmit] = useState(false);

  // const [row, setRow] = useState();
  // const [column, setColumn] = useState();
  const [details, setDetails] = useState({
    row: 0,
    column: 0
  })
  const [error, setError] = useState({
    row: false,
    column: false
  })

  const handleInput = (e) => {
    const { value } = e.target
    setRow((prevError) => ({ ...prevError, row: false }));
    if (value > 0) {
      setError((prevError) => ({ ...prevError, row: false }))
    } else {
      setError((prevError) => ({ ...prevError, row: true }))
    }
  }
  const handleSubmit = () => {
    // if(row>0)
  }

  return (
    <>
      {!formSubmit ? (
        <div className="register-form-wrapper">
          <div className="form-container">

            <div className="form">
              <h2 className="form__heading">Registration</h2>


              {/* row */}
              <div className="form__input">
                <p className="label">Number of Rows</p>
                <div className={`input-container ${(row > 0) && "activeInput"}`}>
                  <input
                    type="number"
                    value={row}
                    min="0"
                    onChange={handleInput}
                  />

                </div>
              </div>
              {
                error.row &&
                <p className="error">Please Enter Company Name</p>
              }


              {/* column */}
              <div className="form__input">
                <p className="label">Number of Columns</p>
                <div className={`input-container ${(column > 0) && "activeInput"}`}>
                  <input
                    type="number"
                    value={column}
                    min="0"
                    onChange={(e) => {
                      setColumn(parseInt(e.target.value));
                    }}
                  /></div>
              </div>
              {
                error.column &&
                <p className="error">Please Enter Company Name</p>
              }


              {/* submit button */}
              <div className="submit-btn-wrapper">
                <div className="submit-btn">
                  <button
                    onClick={() => {
                      setFormSubmit(true);
                    }}
                  >
                    Submit
                  </button>
                </div>

              </div>
            </div>

          </div>
        </div>
      ) : (
        <LayoutDesign row={row} column={column} />
      )}
    </>
  );
};
export default RegisterForm;
