import React, { useContext, useState } from "react";
import { LayoutDesign } from "../LayoutDesign/LayoutDesign";
import { CompanyName } from "../../context/CreateContext";
import { useNavigate } from "react-router-dom";

const RegisterForm = () => {
  const [layouts, setLayouts] = useState([]);
  const [currentLayout, setCurrentLayout] = useState(1);
  const [details, setDetails] = useState({ row: 0, column: 0 });
  const [error, setError] = useState({ row: false, column: false });
  const { companyName } = useContext(CompanyName);
  const navigate = useNavigate();

  const handleInput = (e) => {
    const { name, value } = e.target;
    setDetails((prevDetails) => ({ ...prevDetails, [name]: parseInt(value, 10) }));
    if (value > 0) {
      setError((prevError) => ({ ...prevError, [name]: false }));
    } else {
      setError((prevError) => ({ ...prevError, [name]: true }));
    }
  };

  const handleSubmit = () => {
    if (details.row > 0 && details.column > 0) {
      setLayouts([...layouts, { ...details }]);
      setDetails({ row: 0, column: 0 });
      setCurrentLayout(currentLayout + 1);
    } else {
      if (!(details.row > 0) && !(details.column > 0)) {
        setError({ row: true, column: true });
      } else if (!(details.row > 0)) {
        setError((prevError) => ({ ...prevError, row: true }));
      } else {
        setError((prevError) => ({ ...prevError, column: true }));
      }
    }
  };

  const handleLayoutSubmit = () => {
    navigate("/seating", { state: { layouts, flag: true } });
  };
console.log()
  return (
    <>
      <div className="register-form-wrapper">
        <div className="form-container">
          <div className="form">
            <div className="form__input">
              <p className="label">Enter Rows</p>
              <div className={`input-container ${(error.row || details.row <= 0) && "error"}`}>
                <input
                  name="row"
                  type="number"
                  value={details.row}
                  min="0"
                  onChange={handleInput}
                  placeholder="Enter number of Rows"
                />
              </div>
              {error.row && <p className="error">Row should be greater than one</p>}
            </div>
            <div className="form__input">
              <p className="label">Enter Columns</p>
              <div className={`input-container ${(error.column || details.column <= 0) && "error"}`}>
                <input
                  name="column"
                  type="number"
                  value={details.column}
                  min="0"
                  onChange={handleInput}
                  placeholder="Enter number of columns"
                />
              </div>
              {error.column && <p className="error">Column should be greater than one</p>}
            </div>
            <div className="submit-btn-container">
              <div className="submit-btn">
                <button onClick={handleSubmit}>Submit</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {layouts.map((layout, index) => (
        <div key={index}>
          <h3>Layout {index + 1}</h3>
          <LayoutDesign row={layout.row} column={layout.column} />
        </div>
      ))}
      {layouts.length > 0 && (
        <div className="submit-btn-container">
          <button className="submit-btn" onClick={handleLayoutSubmit}>
            Submit All Layouts
          </button>
        </div>
      )}
    </>
  );
};

export default RegisterForm;
