import "./RegisterForm.scss";
import React, { useContext, useEffect, useState } from "react";
import { LayoutDesign } from "../LayoutDesign/LayoutDesign";
import { CompanyName } from "../../context/CreateContext";
import { useLocation, useNavigate } from "react-router-dom";
const RegisterForm = () => {
  const location=useLocation()
  const layout=location.state.totalLayout
  console.log(layout)
  const [formSubmit, setFormSubmit] = useState(false);
  const navigate = useNavigate()
  const { companyName } = useContext(CompanyName)
  const [totalLayouts, setTotalLayouts] = useState(0)
  const [details, setDetails] = useState({
    row: 0,
    column: 0
  })
  const [error, setError] = useState({
    row: false,
    column: false,
    totalLayouts: false,
  })

  const handleInput = (e) => {
    const { name, value } = e.target
    setDetails((prevDetails) => ({ ...prevDetails, [name]: parseInt(value) }));
    if (value > 0) {
      setError((prevError) => ({ ...prevError, [name]: false }))
    } else {
      setError((prevError) => ({ ...prevError, [name]: true }))
    }
  }
  const handleLayout = (e) => {
    const { name, value } = e.target
    console.log(name)
    setTotalLayouts(value);
    if (value > 0) {
      setError((prevError) => ({ ...prevError, [name]: false }))
    } else {
      setError((prevError) => ({ ...prevError, [name]: true }))
    }
  }
  console.log(totalLayouts, details)
  const handleSubmit = () => {
    if (details.row > 0 && details.column > 0 && totalLayouts > 0) {
      setFormSubmit(true)
    } else {
      setFormSubmit(false)
      if (!(details.row > 0) && !(details.column > 0) && !(totalLayouts > 0)) {
        setError((prevError) => ({ ...prevError, row: true, column: true, totalLayouts: true }))
      } else if (!(details.row > 0)) {
        setError((prevError) => ({ ...prevError, row: true }))
      } else if (!(details.column > 0)) {
        setError((prevError) => ({ ...prevError, column: true }))
      } else {
        console.log("first")
        setError((prevError) => ({ ...prevError, totalLayouts: true }))
      }
    }
  }
  useEffect(() => {
    if (CompanyName === "") {
      navigate("/")
    }
  }, [companyName])
console.log(formSubmit)
  return (
    <>

        <div className="register-form-wrapper">
          <div className="form-container">

            <div className="form">
              {/* <h2 className="form__heading">Registration</h2> */}
              <div className="form__input">
                <p className="label">Total Layouts</p>
                <div className={`input-container ${(totalLayouts > 0) && "activeInput"}`}>
                  <input
                    name="totalLayouts"
                    type="number"
                    value={totalLayouts}
                    min="0"
                    onChange={handleLayout}
                    placeholder="Enter number of Layouts"
                  />
                </div>
              </div>
              {
                error.totalLayouts &&
                <p className="error">Layout should be greater than one</p>
              }

              {/* row */}
              <div className="form__input">
                <p className="label">Enter Rows</p>
                <div className={`input-container ${(details.row > 0) && "activeInput"}`}>
                  <input
                    name="row"
                    type="number"
                    value={details.row}
                    min="0"
                    onChange={handleInput}
                    placeholder="Enter number of Rows"
                  />

                </div>
              </div>
              {
                error.row &&
                <p className="error">Row should be greater than one</p>
              }


              {/* column */}
              <div className="form__input">
                <p className="label">Enter Columns</p>
                <div className={`input-container ${(details.column > 0) && "activeInput"}`}>
                  <input
                    name="column"
                    type="number"
                    value={details.column}
                    min="0"
                    onChange={handleInput}
                    placeholder="Enter number of columns"
                  /></div>
              </div>
              {
                error.column &&
                <p className="error">Column should be greater than one</p>
              }


              {/* submit button */}
              <div className="submit-btn-container">
                <div className="submit-btn">
                  <button onClick={handleSubmit}>
                    Submit
                  </button>
                </div>

              </div>
            </div>

          </div>
        </div>
        {formSubmit &&
        <LayoutDesign row={details.row} column={details.column} />
      }
    </>
  );
};
export default RegisterForm;
