import React, { useEffect, useState } from 'react'
import "./updatePopup.scss"
import { Cell } from '../Cell/Cell'

export const UpdatePopup = ({ layoutSeleted }) => {
    console.log(layoutSeleted)
    const row=3
    const column=3
    const [arrayData, setArrayData] = useState(layoutSeleted.companyLayout);
    const updateValue = (rowIndex, colIndex, value) => {
        const newArrayData = { ...arrayData };
        newArrayData[rowIndex][colIndex] = value;
        setArrayData(newArrayData);
      };
      useEffect(() => {
        const initialArrayData = {};
        for (let i = 0; i < row; i++) {
          initialArrayData[i] = Array(column).fill(0);
        }
        setArrayData(initialArrayData);
      }, [row, column]);
      console.log(arrayData)
    return (
        <div>
            {/* {layoutSeleted?.companyLayout?.map((e) => (
                <p>{e}</p>
            ))} */}
            {layoutSeleted?.companyLayout?.map((row, rowIndex) => (
                <div key={rowIndex} className="row">
                    {row.map((_, colIndex) => (
                        <Cell
                            key={colIndex}
                            updateValue={updateValue}
                            rowIndex={rowIndex}
                            colIndex={colIndex}
                        />
                    ))}
                </div>
            ))}
        </div>
    )
}
