import "./RegisterForm.scss";
import React, { useContext, useEffect, useState } from "react";
import { CompanyName } from "../../context/CreateContext";
import { useNavigate } from "react-router-dom";
const LayoutForm = () => {
//   const [formSubmit, setFormSubmit] = useState(false);
  const navigate = useNavigate()
  const { companyName } = useContext(CompanyName)
  const [totalLayouts, setTotalLayouts] = useState(0)
  const [error, setError] = useState({
    totalLayouts: false,
  })

  const handleLayout = (e) => {
    const { name, value } = e.target
    console.log(name)
    setTotalLayouts(value);
    if (value > 0) {
      setError((prevError) => ({ ...prevError, [name]: false }))
    } else {
      setError((prevError) => ({ ...prevError, [name]: true }))
    }
  }
  console.log(totalLayouts)
  const handleSubmit = () => {
    if (totalLayouts > 0) {
      navigate("/")
    } else {
      if (!(totalLayouts > 0)) { {
        setError((prevError) => ({ ...prevError, totalLayouts: true }))
      }
    }
  }
  useEffect(() => {
    if (CompanyName === "") {
      navigate("/")
    }
  }, [companyName])
  return (
    <>
        <div className="register-form-wrapper">
          <div className="form-container">

            <div className="form">
              <h2 className="form__heading">Registration</h2>
              <div className="form__input">
                <p className="label">Total Layouts</p>
                <div className={`input-container ${(totalLayouts > 0) && "activeInput"}`}>
                  <input
                    name="totalLayouts"
                    type="number"
                    value={totalLayouts}
                    min="0"
                    onChange={handleLayout}
                    placeholder="Enter number of Layouts"
                  />
                </div>
              </div>
              {
                error.totalLayouts &&
                <p className="error">Layout should be greater than one</p>
              }      

              {/* submit button */}
              <div className="submit-btn-container">
                <div className="submit-btn">
                  <button onClick={handleSubmit}>
                    Submit
                  </button>
                </div>

              </div>
            </div>

          </div>
        </div>
    </>
  );
};
}
export default LayoutForm;
