import React, { useEffect } from 'react'

export const RadioInput = ({ number, preference, handlePrefOnClick, label, handleSubmit }) => {
  useEffect(() => {
    const radioHandel = () => {
      handleSubmit()
      handlePrefOnClick(number)
    }
    radioHandel()
  }, [number])
  return (
    <label className="radio-btn">
      <input
        type="radio"
        name="option"
        checked={preference === number}
        onClick={radioHandel}
      />
      {label}
    </label>
  )
}
