import React, { useEffect, useState } from 'react'
import "./NavBar.scss";
import { useLocation, useNavigate } from 'react-router-dom';

const NavBar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;


  const handleLogout = () => {
    navigate("/login")
  };

  const [selectedMenu, setSelectedMenu] = useState("allLayout")
  const handleMenu = (menu) => {
    navigate(`/${menu.toLowerCase()}`);
    setSelectedMenu(menu)
  }

  useEffect(() => {
    if (pathname === '/') setSelectedMenu("allLayout")
    if (pathname === '/"allLayout"') setSelectedMenu("allLayout")
    if (pathname === '/operations') setSelectedMenu("operations")
  }, [pathname])
  return (
    <div className='navbar-wrapper'>
      <div className='NavBar'>
        <div className='navbar-container'>
          <div className='nav-menu-bar'>
            <h1>Seating-Arrangement</h1>
            <div>
              <h3 className='nav-menu' onClick={() => handleMenu("allLayout")}>All Layouts</h3>
              <div className={selectedMenu === "allLayout" && 'nav-menu-hr'}></div>
            </div>
            <div>
              <h3 className='nav-menu' onClick={() => handleMenu("opertaions")}>Opertaions</h3>
              <div className={navMenuSelected === Home && 'nav-menu-hr'}></div>
            </div>
          </div>
          <div >
            <button className='logout-btn' onClick={() => handleLogout}>
              Logout
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default NavBar
