import React, { useContext, useEffect, useState } from 'react'
import "./Operations.scss"
import { useNavigate } from 'react-router-dom'
import { LayoutPopup } from '../../components/LayoutPopup/LayoutPopup'
import { getAlllayout } from '../../actions/ApiCall'
import { CompanyName } from '../../context/CreateContext'

export const Operations = () => {
    const companyName = useContext(CompanyName)

    const [allLayouts, setLayouts] = useState()
    useEffect(() => {
        async function getAllLayouts() {
            try {
                const res = await getAlllayout(companyName.companyName)
                // console.log(res)
                setLayouts(res.data)
                if (res.message === "Company not found") {
                } else {
                }
            } catch (err) {
                console.log(err)
            }
        }
        getAllLayouts()
    }, [companyName])
    const [popup, setPopup] = useState(false)
    useEffect(() => {
        function handle(e) {
            if (e.target.className === "allocation-seating-popup-parent") {
                setPopup(false)
                setLayoutSeleted()
                setOptionSelected("")
                setSubmit(false)
            }
        }
        window.addEventListener("click", handle)
        return () => window.removeEventListener("click", handle)
    }, [])
    const [optionSelected, setOptionSelected] = useState("")
    const [submit, setSubmit] = useState(false)
    const navigate = useNavigate()
    const [layoutSelected, setLayoutSeleted] = useState()
    const handleLayoutSelected = (layout) => {
        setPopup(true)
        setOptionSelected("")
        setSubmit(false)
        setLayoutSeleted(layout)
    }
    useEffect(() => {
        if (submit && optionSelected !== "") {
            if (optionSelected === "Seating") {
                navigate("/seating", {
                    state: {
                        data: layoutSelected.companyLayout,
                        layoutId: layoutSelected.layoutId,
                        availableSpaces: layoutSelected.totalSpace
                    }
                });
            }
        }
    })
    return (
        <div className='operations-page'>
            <div className='add-btn-wrapper'>
                <button className='add-btn'>
                    +  Add
                </button>
            </div>
            <div className='operation-layout-page'>
                <table className="MyTable">
                    <tbody>
                        {allLayouts?.companyLayout?.map((layout, index) => (
                            <div className='layout-list' onClick={() => { handleLayoutSelected(layout) }}>
                                {/* <p className='heading'>Layout {index + 1}</p> */}
                                <div>
                                    {layout?.companyLayout?.map((row, i) => {
                                        return (
                                            <tr key={i} >
                                                {row?.map((value, j) => {
                                                    return (
                                                        <>
                                                            <td
                                                                key={j}
                                                                className="grid-box"
                                                                style={{
                                                                    backgroundColor:
                                                                        value === 1 ? "#3FA9F5" : "#C8C8C8",
                                                                    color:
                                                                        value === 1 ? "#3FA9F5" : "#C8C8C8",
                                                                }}>
                                                                {value}
                                                            </td>
                                                        </>
                                                    );
                                                })}
                                            </tr>
                                        );
                                    })
                                    }
                                </div>
                            </div>
                        ))}

                    </tbody>
                </table>
                {
                    popup &&
                    <div className='allocation-seating-popup-parent'>
                        <div className='allocation-seating-popup'>
                            {/* <LayoutPopup layoutOptions={true} setPopup={setPopup} optionSelected={optionSelected} setOptionSelected={setOptionSelected} setSubmit={setSubmit} /> */}
                        </div>
                    </div>
                }
            </div>
        </div>
    )
}
