import React, { useState } from 'react'
import "./layoutPopup.scss"
export const LayoutPopup = ({ layoutOptions, optionSelected, setOptionSelected, count, setPopup, setSubmit }) => {

    const [error, setError] = useState(false);
    const handleOption= () => {
        if (layoutOptions) {
            console.log(optionSelected==="")
            if (optionSelected === "") {
                setError(true)
            } else {
                setError(false)
                setSubmit(true)
                setPopup(false)
            }
        }
    }
    return (
        <div className='layout-popup'>
            {
                layoutOptions ?
                    <>
                        <p className='p-2'>Select any Options :</p>
                        <div className='options-btn'>
                            <button onClick={()=>{setOptionSelected("Allocation");handleOption()}}>Allocation</button>
                            <button onClick={()=>{setOptionSelected("Seating");handleOption()}}>Seating</button>
                        </div>
                        {
                            error &&
                            <p className='error'>Please provide any option</p>
                        }
                        {/* <div className="home__buttons">
                            <button className="yes-btn" onClick={handleOk}>Submit</button>
                        </div> */}
                    </>
                    :
                    <>
                        <p className='head'>Seating Count  : {count}</p>
                        <p className='p-1'>Click ok to contine</p>
                        <div className="home__buttons">
                            <button className="yes-btn" onClick={()=>setSubmit(true)}>Ok</button>
                            <button className="no-btn" onClick={() => { setPopup(false) }}>Cancel</button>
                        </div>
                    </>
            }
        </div>
    )
}
