import { useContext, useEffect, useState } from "react";
import axios from "axios";
import "./Seating.css";
import { CompanyName } from "../../context/CreateContext";
import { Link, json, useLocation } from "react-router-dom";
import { colorList } from "../../constants/colorList";

const Seating = () => {
  const [isOutputGenerated, setIsOutput] = useState(false);
  const { companyName } = useContext(CompanyName);
  const formData = new FormData();
  const location = useLocation();
  const res = location.state.data;
  const flag = location.state.flag;
  let availableSpaces = location.state.availableSpaces;
  const [outputArray, setOutputArray] = useState();
  const [layOut, setLayOut] = useState(null);
  const [file, setFile] = useState(null);
  const [teamList, setTeamList] = useState([]);
  const [teamNameList, setTeamNameList] = useState([]);
  const [teamKeyList, setTeamKeyList] = useState([]);
  const [preference, setPreference] = useState(2);
  const [orderedTeamList, setOrderedTeamList] = useState([]);
  const [space, setSpace] = useState(availableSpaces);
  const [result, setResult] = useState(null);

  useEffect(() => {
    loadLayOut();
  }, []);

  const loadLayOut = async () => {
    setLayOut(res);
  };

  const handleFile = (e) => {
    setFile(e.target.files[0]);
  };
  formData.append('file', file);

  const handleSubmit = async () => {
    let arr = [];
    teamList.map((team) => {
      arr.push(team.TeamName);
    });
    setTeamNameList(arr);
    const res = await axios.post("http://localhost:8090/allocation", );
    setIsOutput(true);
    setResult(res);
    setOutputArray(res.data.data.allocation);
    let teamKeyList = [];
    res.data.data.teamReferenceList.map((team) => {
      teamKeyList.push(team.key);
    });
    setTeamKeyList(teamKeyList);
    setOrderedTeamList(res.data.data.teamReferenceList);
  };

  const handleCloseBtn = (index) => {
    let arr = [...teamList];
    setSpace(space + arr[index].TeamCount);
    arr = arr.slice(0, index).concat(arr.slice(index + 1, arr.length));
    setTeamList(arr);
  };

  const handleFileSubmit = async () => {
    try {
      const res = await axios.post("http://localhost:8090/csvFile", formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        }
      )
      console.log(res)
    } catch (error) {
      console.log(error)
    }

  }

  const handleAddTeam = () => {
    let arr = [...teamList];
    arr.push({
      TeamName: "",
      TeamCount: "",
    });
    setTeamList(arr);
  };

  const handleOnChange = (e, index) => {
    let arr = [...teamList];
    let spaces = space;
    if (teamList[index].TeamCount === "" && e.target.name === "TeamCount") {
      spaces -= Number(e.target.value);
      setSpace(spaces);
    }
    arr[index] = {
      ...arr[index],
      [e.target.name]:
        e.target.name === "TeamName" ? e.target.value : Number(e.target.value),
    };

    if (teamList[index].TeamCount !== "" && e.target.name === "TeamCount") {
      spaces += teamList[index].TeamCount;
      spaces -= Number(e.target.value);
      setSpace(spaces);
    }
    setTeamList(arr);
  };
  const handleReturnColor = (teamKeyValue) => {
    for (let i = 0; i < teamKeyList.length; i++) {
      if (teamKeyValue && teamKeyValue.includes(teamKeyList[i])) {
        return colorList[i];
      }
    }
    return "grey";
  };

  const handlePrefOnClick = (prefNum) => {
    setPreference(prefNum);
  }


  return (
    <div className="seating">
      <div className="container-1">
        <table className="MyTable">
          <tbody>
            {flag ? (
              <>
                {res.layOut.map((row, i) => {
                  return (
                    <tr key={i}>
                      {row.map((value, j) => {
                        return (
                          <td
                            key={j}
                            className="grid-box"
                            style={{
                              backgroundColor:
                                value === 1 ? "#2ecc71" : "#f1f2f6",
                            }}
                          >
                            {value}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </>
            ) : (
              <>
                {res.map((row, i) => {
                  return (
                    <tr key={i}>
                      {row.map((value, j) => {
                        return (
                          <td
                            key={j}
                            className="grid-box"
                            style={{
                              backgroundColor:
                                value === 1 ? "#2ecc71" : "#f1f2f6",
                            }}>
                            {value}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
              </>
            )}
          </tbody>
        </table>
        <div className="form-wrapper">
          <h3>availableSpaces:{space}</h3>

          <h2>Add Team</h2>

          <div className="btn-wrapper">
            <button className="add-btn" onClick={handleAddTeam}>
              + Add
            </button>
            <label className="radio-title">Count Priority :</label>
            <label className="radio-btn">
              <input
                type="radio"
                name="option"
                checked={preference === 2}
                onClick={() => handlePrefOnClick(2)}
              />
              ASC
            </label>
            <label className="radio-btn">
              <input
                type="radio"
                name="option"
                checked={preference === 1}
                onClick={() => handlePrefOnClick(1)}
              />
              DES
            </label>
            <label className="radio-btn">
              <input
                type="radio"
                name="option"
                checked={preference === 3}
                onClick={() => handlePrefOnClick(3)}
              />
              Random
            </label>
          </div>
          <div className="team-list-input-wrapper">
            <div className="team-list-container">
              {teamList &&
                teamList?.map((data, index) => {
                  return (
                    <div className="input-wrapper">
                      <input
                        type="text"
                        name="TeamName"
                        value={data.TeamName}
                        className="input-box"
                        onChange={(e) => handleOnChange(e, index)}
                        placeholder={"Enter Team " + (index + 1) + " Name"}
                      />
                      <input
                        type="number"
                        name="TeamCount"
                        value={data.TeamCount}
                        min="0"
                        max={availableSpaces}
                        disabled={space <= 0}
                        className="input-box"
                        placeholder={"Enter Team " + (index + 1) + " Count"}
                        onChange={(e) => handleOnChange(e, index)}
                      />

                      <button
                        className="cross-btn"
                        onClick={() => handleCloseBtn(index)}
                      >
                        X
                      </button>
                    </div>
                  );
                })}
              <div className="input-file">
                OR
                <input
                  onChange={handleFile}
                  className="input-box1"
                  type="file"
                />
                <button onClick={handleFileSubmit} className="click-btn">Verify</button>
              </div>
            </div>
          </div>
          <button className="submit-btn" onClick={handleSubmit}>
            Submit
          </button>
        </div>
      </div>
      <div className="container-1">
        <div className="layout-wrapper">
          {isOutputGenerated && (
            <>
              <h2>Team Allocation Layout</h2>
              <table className="MyTable">
                <tbody>
                  {outputArray.map((row, i) => {
                    return (
                      <tr key={i}>
                        {row.map((value, j) => {
                          return (
                            <td
                              key={j}
                              className="grid-box"
                              style={{
                                backgroundColor: flag
                                  ? res.layOut[i][j] === 1
                                    ? handleReturnColor(value)
                                    : "#f1f2f6"
                                  : res?.[i][j] === 1
                                    ? handleReturnColor(value)
                                    : "#f1f2f6",
                              }}
                            >
                              {value}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </>
          )}
        </div>
        {isOutputGenerated && (
          <div className="team-key-continer">
            {teamNameList && <h2>Team Keys</h2>}
            <table className="team-key-list key-table">
              <thead>
                <th>Team Name</th>
                <th>Team Key</th>
              </thead>
              <tbody>
                {orderedTeamList &&
                  orderedTeamList.map((team) => {
                    return (
                      <tr>
                        <td>{team.name}</td>
                        <td>{team.key}</td>

                      </tr>
                    );
                  })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Seating;
